        aim(index, coordinates)
      }

      function listenToTel({
        frequency = 1000 // Frequency in MHz
      }) {
        listen(selectedTelescope, coordinates)
      }

      return (
        <TelescopeArray>
          <div>
            <button onClick={listen}>Listen</button>
            <Telescope coords={} />
          </div>
        </TelescopeArray>
      )
    }

