import Highlight from '../Highlight';

export default function Hero() {

  const code = `

    import TelescopeArray, Telescope, { observe, aim } from '@telescope/core';

    function Observe({ Component, pageProps }) {

      function setAscension({
        index = 0,
        ascension = 120,
      }) {
        console.log(\`Ascension for satelite \${index} changed to \${ascension}\`);
        aim(index, ascension)
      }

      function listen({
        frequency = 1000 // Frequency in MHz
      }) {
        observe(selectedTelescope, ascension)
      }

      return (
        <TelescopeArray>
          <Telescope ascension={120} onChange={setAscension} onActivity={listen} />
        </TelescopeArray>
      )
    }

    export default Observe

  `;
  return (
    <div className="code-sample"> 
      <Highlight language="javascript" content={code} header="JavaScript" />
    </div>
  )
}
