import React, { useEffect } from "react";
import Prism from "prismjs";

const CodeEditor = ({
  content = '',
  header = null,
  language = 'javascript'
}) => {

  // useEffect(() => {
  //   Prism.highlightAll();
  // }, [language, content]);

  Prism.highlightAll();

  return (
    <div className="highlight"> 
      {header && <h1>{header}</h1>}
      <pre>
        <code className={`language-${language}`}>{content}</code>
      </pre>
    </div>
  );
};

export default CodeEditor;
